INSERT INTO `information_description` (`information_id`, `language_id`, `name`, `description`, `name_seo`, `meta_description`, `keywords`) VALUES ('36', '5', 'Ch&iacute;nh s&aacute;ch vận chuyển', '&lt;p&gt;\r\n\t&lt;span style=&quot;color:#000;&quot;&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;em&gt;Với phương ch&amp;acirc;m thuận tiện v&amp;agrave; nhanh ch&amp;oacute;ng cho kh&amp;aacute;ch h&amp;agrave;ng trong việc nhận h&amp;agrave;ng&lt;/em&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n\t&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;span style=&quot;color:#f00;&quot;&gt;&lt;strong&gt;&lt;em&gt;TECH ONE k&amp;iacute;nh mong Qu&amp;yacute; kh&amp;aacute;ch h&amp;agrave;ng lưu &amp;yacute; những điểm sau :&lt;/em&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n\t&amp;nbsp;&lt;/p&gt;\r\n&lt;p&gt;\r\n\t&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size:12px;&quot;&gt;&lt;span style=&quot;color: rgb(105, 105, 105); text-indent: -0.25in;&quot;&gt;-&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;color: rgb(105, 105, 105); text-indent: -0.25in; font-style: italic;&quot;&gt;Gi&amp;aacute; tr&amp;ecirc;n sản phẩm kh&amp;ocirc;ng bao gồm ph&amp;iacute; vận chuyển, nếu ph&amp;aacute;t sinh vận chuyển sẽ t&amp;iacute;nh sau&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n\t&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;color:#696969;&quot;&gt;&lt;em&gt;- Trong nội th&amp;agrave;nh H&amp;agrave; Nội : miễn ph&amp;iacute; chuyển h&amp;agrave;ng trong phạm vi 15km t&amp;iacute;nh từ Showroom của TechOne, nếu phạm vi giao h&amp;agrave;ng lớn hơn 15km, qu&amp;yacute; kh&amp;aacute;ch vui l&amp;ograve;ng thanh to&amp;aacute;n cước vận chuyển cho TechOne qua việc trao đổi với nh&amp;acirc;n vi&amp;ecirc;n của ch&amp;uacute;ng t&amp;ocirc;i.&lt;/em&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n\t&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size:12px;&quot;&gt;&lt;span style=&quot;color:#696969;&quot;&gt;&lt;em&gt;- Ngoại th&amp;agrave;nh v&amp;agrave; ngoại tỉnh, thanh to&amp;aacute;n trước bằng chuyển khoản ng&amp;acirc;n h&amp;agrave;ng hoặc gửi tiền.&lt;/em&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n&lt;p&gt;\r\n\t&lt;span style=&quot;font-family:times new roman,times,serif;&quot;&gt;&lt;span style=&quot;font-size:12px;&quot;&gt;&lt;span style=&quot;color:#696969;&quot;&gt;&lt;em&gt;- Một số mẫu h&amp;agrave;ng chỉ c&amp;oacute; 1 chiếc hoặc số lượng hạn chế, trong trường hợp nh&amp;acirc;n vi&amp;ecirc;n TechOne hỏi x&amp;aacute;c nhận th&amp;ocirc;ng tin mua h&amp;agrave;ng, Qu&amp;yacute; kh&amp;aacute;ch h&amp;agrave;ng kh&amp;ocirc;ng x&amp;aacute;c nhận th&amp;igrave; TechOne sẽ chuyển mẫu h&amp;agrave;ng đ&amp;oacute; ngay cho c&amp;aacute;c kh&amp;aacute;ch h&amp;agrave;ng sau đ&amp;oacute;.&lt;/em&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n', '', '', '');
