INSERT INTO `information_description` (`information_id`, `language_id`, `name`, `description`, `name_seo`, `meta_description`, `keywords`) VALUES ('15', '5', 'Tuyển dụng', '&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#f00&quot;&gt;&lt;span style=&quot;font-family:times new roman,times,serif&quot;&gt;&lt;em&gt;&lt;span style=&quot;font-size:18px&quot;&gt;&lt;strong&gt;TechOne đ&amp;atilde; tuyển dụng đủ vị tr&amp;iacute; nh&amp;acirc;n sự&amp;nbsp;&lt;/strong&gt;&lt;/span&gt;&lt;/em&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;font-size:14px&quot;&gt;&lt;span style=&quot;color:#696969&quot;&gt;&lt;em&gt;&lt;span style=&quot;font-family:times new roman,times,serif&quot;&gt;Hiện nay ch&amp;uacute;ng t&amp;ocirc;i đang mở rộng v&amp;agrave;o thị trường ph&amp;iacute;a Nam, TechOne cần tuyển cho chi nh&amp;aacute;nh ph&amp;iacute;a Nam những vị tr&amp;iacute; dưới đ&amp;acirc;y &lt;/span&gt;&lt;/em&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:#(color)&quot;&gt;&lt;span style=&quot;font-family:times new roman,times,serif&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size:14px&quot;&gt;&lt;em&gt;&lt;u&gt;1/ Nh&amp;acirc;n vi&amp;ecirc;n b&amp;aacute;n h&amp;agrave;ng: số lượng 5&lt;/u&gt;&lt;/em&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&lt;span style=&quot;font-family:times new roman,times,serif&quot;&gt;&lt;span style=&quot;color:#696969&quot;&gt;- M&amp;ocirc; tả c&amp;ocirc;ng việc:&amp;nbsp; B&amp;aacute;n h&amp;agrave;ng v&amp;agrave; kinh doanh tại showroom của c&amp;ocirc;ng ty; Tư vấn v&amp;agrave; x&amp;acirc;y dựng c&amp;aacute;c chương tr&amp;igrave;nh; Tư vấn sản phẩm cho kh&amp;aacute;ch h&amp;agrave;ng.&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;&l