INSERT INTO `information_description` (`information_id`, `language_id`, `name`, `description`, `name_seo`, `meta_description`, `keywords`) VALUES ('75', '5', 'Ph&acirc;̀n m&ecirc;̀m Smartphone N&acirc;ng Cao', '&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;http://congnghe.tuvantuyensinh.vn/uploads/news/thuc-1986/2013/11/08/h1.jpg&quot; style=&quot;display:block; height:350px; margin-left:auto; margin-right:auto; width:590px&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;color:rgb(128, 128, 128)&quot;&gt;&lt;em&gt;&lt;span style=&quot;font-family:times new roman,times; font-size:medium&quot;&gt;So s&amp;aacute;nh tốc độ xử l&amp;yacute; của iPhone 5S, Samsung Galaxy S4 v&amp;agrave; Si&amp;ecirc;u phẩm LG G2&lt;/span&gt;&lt;/em&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:center&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:rgb(255, 0, 0); font-family:times new roman,times; font-size:large&quot;&gt;&lt;strong&gt;1. Giới thiệu chương tr&amp;igrave;nh đ&amp;agrave;o tạo&lt;/strong&gt;&lt;strong&gt;:&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n\t&lt;li&gt;&lt;span style=&quot;color:rgb(128, 128, 128); font-family:times new roman,times; font-size:large&quot;&gt;Chuy&amp;ecirc;n đề Phần mềm Smartphone chuy&amp;ecirc;n s&amp;acirc;u là Module đào tạo đặc bi&amp;ecirc;̣t, dành cho các học vi&amp;ecirc;n đã hoàn thành&amp;nbsp;&lt;em&gt;N&amp;ocirc;̣i dung Chuy&amp;ecirc;n đ&amp;ecirc;̀ Ph&amp;acirc;̀n m&amp;ecirc;̀m cơ bản và có mong mu&amp;ocirc;́n nghi&amp;ecirc;n cứu s&amp;acirc;u v&amp;ecirc;̀ Ph&amp;acirc;̀n m&amp;ecirc;̀m Smartphone.&lt;/em&gt;&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;ul&gt;\r\n\t&lt;li&gt;&lt;span style=&quot;font-family:times new roman,times; font-size:large&quot;&gt;&lt;span style=&quot;color:rgb(128, 128, 128)&quot;&gt;Trong Chuy&amp;ecirc;n đ&amp;ecirc;̀ này, học vi&amp;ecirc;n sẽ được học trực ti&amp;ecirc;́p với những dòng máy cao c&amp;acirc;́p nh&amp;acirc;́t hi&amp;ecirc;̣n nay như iPhone,&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;color:rgb(128, 128, 128)&quot;&gt;Samsung&lt;/span&gt;&lt;span style=&quot;color:rgb(128, 128, 128)&quot;&gt;&amp;hellip;&lt;/span&gt;&lt;/span&gt;&lt;/li&gt;\r\n\t&lt;li&gt;&lt;span style=&quot;color:rgb(128, 128, 128); font-family:times new roman,times; font-size:large&quot;&gt;Ph&amp;acirc;̀n m&amp;ecirc;̀m là linh h&amp;ocirc;̀n của ĐTDĐ, 80% b&amp;ecirc;̣nh của các dòng máy cao c&amp;acirc;́p t&amp;acirc;̣p trung chủ y&amp;ecirc;́u vào ph&amp;acirc;̀n m&amp;ecirc;̀m. Vì v&amp;acirc;̣y đ&amp;acirc;y là chuy&amp;ecirc;n đ&amp;ecirc;̀ kh&amp;ocirc;ng th&amp;ecirc;̉ thi&amp;ecirc;́u đ&amp;ocirc;́i với các Kỹ thu&amp;acirc;̣t vi&amp;ecirc;n sửa chữa ĐTDĐ.&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:rgb(255, 0, 0); font-family:times new roman,times; font-size:large&quot;&gt;&lt;strong&gt;2. Nội dung chương tr&amp;igrave;nh:&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n\t&lt;li&gt;&lt;span style=&quot;font-family:times new roman,times; font-size:large&quot;&gt;&lt;span style=&quot;color:rgb(128, 128, 128)&quot;&gt;Giới thi&amp;ecirc;̣u v&amp;ecirc;̀ H&amp;ecirc;̣ đi&amp;ecirc;̀u hành các dòng sản ph&amp;acirc;̉m cao c&amp;acirc;́p như:&amp;nbsp;&lt;span style=&quot;color:rgb(51, 51, 153)&quot;&gt;iPhone (3 - 5)&amp;nbsp; &amp;ndash; Samsung Galaxy d&amp;ograve;ng S, d&amp;ograve;ng Note...&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;color:rgb(51, 51, 153)&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/li&gt;\r\n\t&lt;li&gt;&lt;span style=&quot;font-family:times new roman,times; font-size:large&quot;&gt;&lt;span style=&quot;color:rgb(128, 128, 128)&quot;&gt;Cài đặt ph&amp;acirc;̀n m&amp;ecirc;̀m ứng dụng cho:&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;color:rgb(128, 128, 128)&quot;&gt;&lt;span style=&quot;color:rgb(51, 51, 153)&quot;&gt;iOS, Adroid, Windown Phone&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;color:rgb(51, 51, 153)&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/li&gt;\r\n\t&lt;li&gt;&lt;span style=&quot;color:rgb(128, 128, 128); font-family:times new roman,times; font-size:large&quot;&gt;Chạy chương trình Ph&amp;acirc;̀n m&amp;ecirc;̀m &amp;ndash; nạp file Flash.&lt;/span&gt;&lt;/li&gt;\r\n\t&lt;li&gt;&lt;span style=&quot;color:rgb(128, 128, 128); font-family:times new roman,times; font-size:large&quot;&gt;Phương pháp Upload ROM &amp;ndash; N&amp;acirc;ng c&amp;acirc;́p ROM.&lt;/span&gt;&lt;/li&gt;\r\n\t&lt;li&gt;&lt;span style=&quot;color:rgb(128, 128, 128); font-family:times new roman,times; font-size:large&quot;&gt;Cài đặt ti&amp;ecirc;́ng Vi&amp;ecirc;̣t cho các dòng máy cao c&amp;acirc;́p.&lt;/span&gt;&lt;/li&gt;\r\n\t&lt;li&gt;&lt;span style=&quot;color:rgb(128, 128, 128); font-family:times new roman,times; font-size:large&quot;&gt;Sửa chữa các l&amp;ocirc;̃i nghi&amp;ecirc;m trọng v&amp;ecirc;̀ Ph&amp;acirc;̀n m&amp;ecirc;̀m.&lt;/span&gt;&lt;/li&gt;\r\n\t&lt;li&gt;&lt;span style=&quot;font-family:times new roman,times; font-size:large&quot;&gt;&lt;span style=&quot;color:rgb(128, 128, 128)&quot;&gt;Phương ph&amp;aacute;p Giải m&amp;atilde; (Unlock) cho d&amp;ograve;ng m&amp;aacute;y:&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;color:rgb(128, 128, 128)&quot;&gt;&lt;span style=&quot;color:rgb(51, 51, 153)&quot;&gt;Apple, Samsung, LG, HTC...&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;color:rgb(51, 51, 153)&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:rgb(255, 0, 0); font-family:times new roman,times; font-size:large&quot;&gt;&lt;strong&gt;3. Kết th&amp;uacute;c kh&amp;oacute;a học:&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n\t&lt;li&gt;&lt;span style=&quot;font-family:times new roman,times; font-size:large&quot;&gt;&lt;span style=&quot;color:rgb(128, 128, 128)&quot;&gt;Học vi&amp;ecirc;n có th&amp;ecirc;̉ thao tác thu&amp;acirc;̀n thục các v&amp;acirc;́n đ&amp;ecirc;̀ li&amp;ecirc;n quan đ&amp;ecirc;́n ph&amp;acirc;̀n m&amp;ecirc;̀m tr&amp;ecirc;n các dòng máy đời mới như iPhone,&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;color:rgb(128, 128, 128)&quot;&gt;Samsung&lt;/span&gt;&lt;span style=&quot;color:rgb(128, 128, 128)&quot;&gt;&amp;hellip; Ch&amp;acirc;̉n đoán nhanh t&amp;acirc;́t cả các loai b&amp;ecirc;̣nh li&amp;ecirc;n quan đ&amp;ecirc;́n l&amp;ocirc;̃i ph&amp;acirc;̀n m&amp;ecirc;̀m h&amp;ecirc;̣ th&amp;ocirc;́ng.&lt;/span&gt;&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:rgb(0, 51, 0); font-family:times new roman,times; font-size:large&quot;&gt;&lt;span style=&quot;color:rgb(255, 0, 0)&quot;&gt;&lt;strong&gt;4. Thời gian đ&amp;agrave;o tạo:&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/strong&gt;&lt;/span&gt;&lt;span style=&quot;color:rgb(128, 128, 128)&quot;&gt;&lt;strong&gt;100 tiết.&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:rgb(128, 128, 128); font-family:times new roman,times; font-size:large&quot;&gt;Bạn c&amp;oacute; thể lựa chọn thời gian học rất linh hoạt theo điều kiện của m&amp;igrave;nh, cụ thể:&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n\t&lt;li&gt;&lt;span style=&quot;color:rgb(128, 128, 128); font-family:times new roman,times; font-size:large&quot;&gt;Lớp c&amp;acirc;́p t&amp;ocirc;́c, học li&amp;ecirc;n tục từ T2 &amp;ndash; T6 hàng tu&amp;acirc;̀n.&lt;/span&gt;&lt;/li&gt;\r\n\t&lt;li&gt;&lt;span style=&quot;color:rgb(128, 128, 128); font-family:times new roman,times; font-size:large&quot;&gt;Lớp bu&amp;ocirc;̉i t&amp;ocirc;́i:&amp;nbsp; Học tuần 03 buổi (04 tiết/buổi), thứ 2,4 ,6 hoặc 3,5,7.&lt;em&gt;&amp;nbsp;&lt;/em&gt;&lt;em&gt;&amp;nbsp;&lt;/em&gt;&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:rgb(0, 51, 0); font-family:times new roman,times; font-size:large&quot;&gt;&lt;strong&gt;&lt;span style=&quot;color:rgb(255, 0, 0)&quot;&gt;5.&amp;nbsp; Học ph&amp;iacute; trọn kh&amp;oacute;a:&amp;nbsp;&amp;nbsp;&lt;/span&gt;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;color:rgb(153, 51, 0)&quot;&gt;5.900.000 VNĐ&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;color:rgb(128, 128, 128)&quot;&gt;&lt;strong&gt;&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;&amp;nbsp;&lt;/strong&gt;(Đ&amp;oacute;ng 01 lần).&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;\r\n', '', '', '');
INSERT INTO `information_description` (`information_id`, `language_id`, `name`, `description`, `name_seo`, `meta_description`, `keywords`) VALUES ('32', '5', 'Hướng dẫn mua h&agrave;ng online', '&lt;p&gt;&lt;strong&gt;HƯỚNG DẪN MUA H&amp;Agrave;NG ONLINE&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;&lt;em&gt;C&amp;Aacute;CH 1 : ĐẶT H&amp;Agrave;NG TRỰC TIẾP QUA ĐIỆN THOẠI, E-MAIL, YAHOO&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;margin-left:27pt&quot;&gt;&lt;em&gt;&lt;/em&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;em&gt;Gọi điện thoại cho ch&amp;uacute;ng t&amp;ocirc;i theo số :&lt;/em&gt;&amp;nbsp;&lt;strong&gt;&lt;em&gt;Hotline&amp;nbsp;&lt;/em&gt;&lt;/strong&gt;&lt;em&gt;&lt;strong&gt;:&lt;/strong&gt;&lt;/em&gt;&amp;nbsp;&lt;strong&gt;&lt;em&gt;1900.6666 ext: 2&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;margin-left:27pt&quot;&gt;&lt;em&gt;&lt;/em&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;em&gt;Gửi Email đặt h&amp;agrave;ng tới địa chỉ:&lt;/em&gt;&amp;nbsp;&lt;strong&gt;&lt;em&gt;support@techone.vn&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;Khi li&amp;ecirc;n hệ, Qu&amp;yacute; kh&amp;aacute;ch h&amp;agrave;ng vui l&amp;ograve;ng cung cấp th&amp;ocirc;ng tin về sản phẩm, địa chỉ giao h&amp;agrave;ng, số điện thoại (di đ&amp;ocirc;ng) v&amp;agrave; thời điểm giao h&amp;agrave;ng (nếu trong nội th&amp;agrave;nh H&amp;agrave; Nội). Ch&amp;uacute;ng t&amp;ocirc;i sẽ li&amp;ecirc;n hệ ngay v&amp;agrave; chuyển h&amp;agrave;ng theo y&amp;ecirc;u cầu của qu&amp;yacute; kh&amp;aacute;ch h&amp;agrave;ng.&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;&lt;em&gt;C&amp;Aacute;CH 2 : ĐẶT H&amp;Agrave;NG QUA WEBSITE&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;&amp;nbsp;&lt;strong&gt;Bước 1: Dạo shop xem h&amp;a