INSERT INTO `information_description` (`information_id`, `language_id`, `name`, `description`, `name_seo`, `meta_description`, `keywords`) VALUES ('76', '5', 'Chuy&ecirc;n đ&ecirc;̀ Ph&acirc;̀n m&ecirc;̀m iPhone', '&lt;p style=&quot;text-align:center&quot;&gt;&lt;img alt=&quot;&quot; src=&quot;http://daotao.cps.vn/files/Tintuc-Congnghe/Dien_thoai_di_dong/09-2010/iphone-4.jpg&quot; style=&quot;height:305px; width:389px&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:rgb(255, 0, 0); font-family:times new roman,times; font-size:large&quot;&gt;&lt;strong&gt;1. Giới thiệu Chuy&amp;ecirc;n đ&amp;ecirc;̀:&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;div&gt;\r\n&lt;ul&gt;\r\n\t&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:rgb(136, 136, 136); font-family:times new roman,times; font-size:large&quot;&gt;iPhone là sản ph&amp;acirc;̉m c&amp;ocirc;ng ngh&amp;ecirc;̣ cao của hãng Apple (Hoa Kỳ).&amp;nbsp;Phần mềm ch&amp;iacute;nh l&amp;agrave; linh hồn của thiết bị. CPS Vietnam đã chính thức tri&amp;ecirc;̉n khai Chuy&amp;ecirc;n đ&amp;ecirc;̀ đào tạo đặc bi&amp;ecirc;̣t chuy&amp;ecirc;n s&amp;acirc;u v&amp;ecirc;̀ Ph&amp;acirc;̀n m&amp;ecirc;̀m Iphone.&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;ul&gt;\r\n\t&lt;li style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;font-size:x-small&quot;&gt;&lt;span style=&quot;color:rgb(136, 136, 136); font-family:times new roman,times; font-size:large&quot;&gt;Thời gian đào tạo:&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;strong&gt;60 ti&amp;ecirc;́t&lt;/strong&gt;&amp;nbsp; (Học từ thứ 2 - thứ 6, m&amp;ocirc;̃i bu&amp;ocirc;̉i 4 ti&amp;ecirc;́t).&lt;/span&gt;&lt;/span&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;p&gt;&lt;span style=&quot;color:rgb(255, 0, 0); font-family:times new roman,times; font-size:large&quot;&gt;&lt;strong&gt;2. Nội dung chương tr&amp;igrave;nh:&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n\t&lt;li&gt;&lt;span style=&quot;font-size:x-small&quot;&gt;&lt;span style=&quot;color:rgb(0, 51, 0); font-family:times new roman,times; font-size:large&quot;&gt;&lt;span style=&quot;color:rgb(136, 136, 136)&quot;&gt;Giới thi&amp;ecirc;̣u t&amp;ocirc;̉ng quát v&amp;ecirc;̀ d&amp;ograve;ng iPhone (Sản phẩm của Apple)&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/li&gt;\r\n\t&lt;li&gt;&lt;span style=&quot;font-size:x-small&quot;&gt;&lt;span style=&quot;color:rgb(0, 51, 0); font-family:times new roman,times; font-size:large&quot;&gt;&lt;span style=&quot;color:rgb(136, 136, 136)&quot;&gt;Các c&amp;ocirc;ng ngh&amp;ecirc;̣ hãng Apple ứng dụng vào sản ph&amp;acirc;̉m iPhone.&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/li&gt;\r\n\t&lt;li&gt;&lt;span style=&quot;font-size:x-small&quot;&gt;&lt;span style=&quot;color:rgb(0, 51, 0); font-family:times new roman,times; font-size:large&quot;&gt;&lt;span style=&quot;color:rgb(136, 136, 136)&quot;&gt;Kỹ thu&amp;acirc;̣t C&amp;agrave;i đặt Hệ Điều h&amp;agrave;nh iOS!&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/li&gt;\r\n\t&lt;li&gt;&lt;span style=&quot;font-size:x-small&quot;&gt;&lt;span style=&quot;color:rgb(0, 51, 0); font-family:times new roman,times; font-size:large&quot;&gt;&lt;span style=&quot;color:rgb(136, 136, 136)&quot;&gt;Chạy chương tr&amp;igrave;nh, sửa lỗi chương tr&amp;igrave;nh.&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/li&gt;\r\n\t&lt;li&gt;&lt;span style=&quot;font-size:x-small&quot;&gt;&lt;span style=&quot;color:rgb(0, 51, 0); font-family:times new roman,times; font-size:large&quot;&gt;&lt;span style=&quot;color:rgb(136, 136, 136)&quot;&gt;Mở Lock m&amp;aacute;y (M&amp;aacute;y bị kh&amp;oacute;a m&amp;atilde;).&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/li&gt;\r\n\t&lt;li&gt;&lt;span style=&quot;font-size:x-small&quot;&gt;&lt;span style=&quot;color:rgb(0, 51, 0); font-family:times new roman,times; font-size:large&quot;&gt;&lt;span style=&quot;color:rgb(136, 136, 136)&quot;&gt;Kỹ thu&amp;acirc;̣t bẻ khóa c&amp;aacute;c d&amp;ogra