INSERT INTO `product_description` (`product_id`, `language_id`, `name`, `meta_description`, `description`, `tags`, `brief_description`, `technical_description`, `promotion`, `name_seo`, `promotion_hc`) VALUES ('2073', '5', 'Samsung Galaxy Note Pro 12.2 3G 32GB (P901)', 'Samsung Galaxy Note Pro 12.2 3G 32GB (P901)', '&lt;div&gt;\r\n&lt;h2&gt;Sắc r&amp;otilde; điểm ảnh, cho&amp;aacute;ng ngợp khung h&amp;igrave;nh&lt;/h2&gt;\r\n&lt;img alt=&quot;&quot; src=&quot;http://www.techone.vn/image/cache/upload/product/2014/3/13/2073/2073_img1.jpg&quot; style=&quot;border:0px none; vertical-align:middle&quot; title=&quot;&quot; /&gt;\r\n&lt;div&gt;H&amp;atilde;y l&amp;agrave;m việc một c&amp;aacute;ch chuy&amp;ecirc;n nghiệp hơn với GALAXY NotePRO cho d&amp;ugrave; bạn đang xem một chương tr&amp;igrave;nh hay đang chuẩn bị cho buổi thuyết tr&amp;igrave;nh. M&amp;agrave;n h&amp;igrave;nh hiển thị 12.2&amp;quot; WQXGA (2560 x 1600) cho bạn nhiều trải nghiệm c&amp;ugrave;ng với m&amp;agrave;u sắc r&amp;otilde; n&amp;eacute;t v&amp;agrave; th&amp;ocirc;ng tin chi tiết hơn hẳn. M&amp;agrave;n h&amp;igrave;nh linh hoạt n&amp;agrave;y cho ph&amp;eacute;p bạn lật những trang b&amp;aacute;o kỹ thuật số, xem lại những văn bản l&amp;agrave;m việc hay tận hưởng những bộ phim y&amp;ecirc;u th&amp;iacute;ch với hệ thống loa k&amp;eacute;p.&lt;/div&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;div&gt;\r\n&lt;div&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.techone.vn/image/cache/upload/product/2014/3/13/2073/2073_img2.jpg&quot; style=&quot;border:0px none; float:left; padding-right:25px; vertical-align:middle&quot; title=&quot;&quot; /&gt;\r\n&lt;h2&gt;Đa nhiệm c&amp;ugrave;ng l&amp;uacute;c, tăng tốc tối ưu&lt;/h2&gt;\r\n\r\n&lt;div&gt;D&amp;agrave;n đa t&amp;aacute;c vụ bằng 4 c&amp;aacute;ch kh&amp;aacute;c nhau&lt;br /&gt;\r\n&lt;br /&gt;\r\nThay v&amp;igrave; phải chuyển đổi qua lại giữa c&amp;aacute;c chức năng v&amp;agrave; ứng dụng, bạn c&amp;oacute; thể xem nhiều cửa sổ v&amp;agrave; l&amp;agrave;m nhiều việc c&amp;ugrave;ng một l&amp;uacute;c. Lướt web, xem video, chỉnh sửa văn bản hay chơi game c&amp;ugrave;ng l&amp;uacute;c. Bạn nghĩ thế n&amp;agrave;o về hiệu quả?&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;div&gt;\r\n&lt;h2&gt;E-Meeting&lt;/h2&gt;\r\n&lt;img alt=&quot;&quot; src=&quot;http://www.techone.vn/image/cache/upload/product/2014/3/13/2073/2073_img3.jpg&quot; style=&quot;border:0px none; vertical-align:middle&quot; title=&quot;&quot; /&gt;\r\n&lt;div&gt;Mọi cuộc họp trở n&amp;ecirc;n dễ d&amp;agrave;ng hơn bao giờ hết. Kh&amp;ocirc;ng cần giấy viết hay m&amp;aacute;y chủ.&lt;br /&gt;\r\n&lt;br /&gt;\r\nCuộc họp trở n&amp;ecirc;n hiệu quả hơn v&amp;agrave; th&amp;acirc;n thiện với m&amp;ocirc;i trường hơn khi m&amp;agrave; tất cả được hỗ trợ bằng e-Meeting, sự hỗ trợ sắp xếp c&amp;aacute; nh&amp;acirc;n với khả năng chia sẻ ấn tượng. Với điểm truy cập mạng di động đơn giản hay kết nối WiFi, bạn c&amp;oacute; thể kết nối với tất cả những người tham gia cuộc họp bằng c&amp;aacute;ch chia sẻ dữ liệu c&amp;ugrave;ng một l&amp;uacute;c v&amp;agrave; ghi ch&amp;uacute; lại nội dung ở c&amp;ugrave;ng một nơi&lt;/div&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;div&gt;\r\n&lt;h2&gt;Air Command (T&amp;aacute;c lệnh kh&amp;ocirc;ng chạm)&lt;/h2&gt;\r\n&lt;img alt=&quot;&quot; src=&quot;http://www.techone.vn/image/cache/upload/product/2014/3/13/2073/2073_img4.jpg&quot; style=&quot;border:0px none; vertical-align:middle&quot; title=&quot;&quot; /&gt;\r\n&lt;div&gt;Lu&amp;ocirc;n sẵn s&amp;agrave;ng nhận lệnh theo c&amp;aacute;ch PRO&lt;br /&gt;\r\n&lt;br /&gt;\r\nChỉ cần nhấp b&amp;uacute;t S Pen l&amp;agrave; bạn sẽ đến được điểm đến tiếp theo. Giữ b&amp;uacute;t S Pen ở bất kỳ đ&amp;acirc;u tr&amp;ecirc;n m&amp;agrave;n h&amp;igrave;nh, sau đ&amp;oacute; nhấn v&amp;agrave;o n&amp;uacute;t tr&amp;ecirc;n th&amp;acirc;n b&amp;uacute;t để c&amp;oacute; thể truy cập ngay v&amp;agrave;o một trong năm ứng dụng hữu &amp;iacute;ch của S Pen bao gồm Action Memo, Scrapbooker, Screen Write, S Finder v&amp;agrave; Pen Window&lt;br /&gt;\r\n&lt;br /&gt;\r\n* H&amp;igrave;nh ảnh mang t&amp;iacute;nh minh họa&lt;/div&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;div&gt;\r\n&lt;div&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.techone.vn/image/cache/upload/product/2014/3/13/2073/2073_img5.jpg&quot; style=&quot;border:0px none; float:left; padding-right:25px; vertical-align:middle&quot; title=&quot;&quot; /&gt;\r\n&lt;h2&gt;Action Memo (Ghi ch&amp;uacute; tức th&amp;igrave;)&lt;/h2&gt;\r\n\r\n&lt;div&gt;Ghi lại những ghi ch&amp;uacute; ngay tức th&amp;igrave; hay sau đ&amp;oacute;. Bằng việc li&amp;ecirc;n kết memo v&amp;agrave; action, bạn c&amp;oacute; thể thực hiện nhiều việc kh&amp;aacute;c nhau như gọi điện thoại hay t&amp;igrave;m kiếm địa chỉ m&amp;agrave; bạn vừa ghi ch&amp;uacute;.&lt;br /&gt;\r\n&lt;br /&gt;\r\n* H&amp;igrave;nh ảnh chỉ c&amp;oacute; t&amp;iacute;nh minh họa&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;div&gt;\r\n&lt;div&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.techone.vn/image/cache/upload/product/2014/3/13/2073/2073_img6.jpg&quot; style=&quot;border:0px none; float:left; padding-right:25px; vertical-align:middle&quot; title=&quot;&quot; /&gt;\r\n&lt;h2&gt;Screen Write (Ghi ch&amp;uacute; trực tiếp l&amp;ecirc;n ảnh chụp m&amp;agrave;n h&amp;igrave;nh)&lt;/h2&gt;\r\n\r\n&lt;div&gt;Nhanh ch&amp;oacute;ng chụp lại m&amp;agrave;n h&amp;igrave;nh v&amp;agrave; ghi ch&amp;uacute; th&amp;ocirc;ng tin tr&amp;ecirc;n đ&amp;oacute; trước khi lưu ch&amp;uacute;ng lại v&amp;agrave;o thư viện ảnh hay chia sẻ th&amp;ocirc;ng qua tin nhắn SMS, gửi mail hay th&amp;ocirc;ng qua c&amp;aacute;c ứng dụng kh&amp;aacute;c.&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;div&gt;\r\n&lt;div&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.techone.vn/image/cache/upload/product/2014/3/13/2073/2073_img7.jpg&quot; style=&quot;border:0px none; float:left; padding-right:25px; vertical-align:middle&quot; title=&quot;&quot; /&gt;\r\n&lt;h2&gt;Pen Window (Vẽ để thực hiện t&amp;aacute;c lệnh đơn giản)&lt;/h2&gt;\r\n\r\n&lt;div&gt;Sử dụng S Pen để khởi tạo t&amp;aacute;c lệnh đơn giản mang đến cho bạn một định nghĩa ho&amp;agrave;n to&amp;agrave;n mới về vận h&amp;agrave;nh đa nhiệm.&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;/div&gt;\r\n', 'Samsung Galaxy Note Pro 12.2 3G 32GB (P901)', '&lt;p&gt;Hộp m&amp;aacute;y, th&amp;acirc;n m&amp;aacute;y, adapter sạc, c&amp;aacute;p micro usb, tai nghe, s&amp;aacute;ch hướng dẫn&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;http://www.techone.vn/image/cache/upload/data/may-tinh-bang/samsung/galaxy-note-pro-12.2/samsung-galaxy-note-355x220-pro.jpg&quot; /&gt;&lt;/p&gt;\r\n', '', '', 'Samsung Galaxy Note Pro 12.2 3G 32GB (P901)', '');
